 import '../App.css';
 import '../normal.css';
 import { useState }  from 'react';
 import ChatBox from '../components/ChatBox';
  import Hamburger from 'hamburger-react';


 function Chat() {

   const [input, setInput] = useState('');
   const [chatLog, setChatLog] = useState([]);
   const [isLoading, setIsLoading] = useState(false);

   function showNavbar() {
    const navbar = document.querySelector('.chat-nav');
    navbar.style.display === 'flex' ? navbar.style.display = 'none' :
    navbar.style.display = 'flex';
   }

 function clearChat() {
   setChatLog([]);
 }

 function sendToAdminPage() {
   window.location.href = '/admin';
 }

 async function handleAdminSubmit(e) {
  e.preventDefault();
  setIsUploadingt(true);

  const formData = new FormData();
  formData.append('file', file);

    const response = await fetch('http://localhost:3080/file', {
    method: 'POST',
    body: formData
    });
    const data = await response.json();
      console.log(data);
      setIsUploading(false);
}


 async function handleSubmit(e) {
     e.preventDefault();
     let chatLogNew = [...chatLog, { user: "me", message: `${input}`}];
     setInput('');
     setChatLog(chatLogNew);
     setIsLoading(true);

     const messages = chatLogNew.map((message) => message.message);  
     const response = await fetch('http://localhost:3080/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ messages }),
      });
      const data = await response.json();
      setChatLog([...chatLogNew, { user: 'gpt', message: data.message }]);
      setIsLoading(false);
 }

   return (
    
     <div className="App">
      <div className='nav'><Hamburger onToggle={showNavbar} color='#fff' size={20} /></div>
      <div className='chat-nav'>
        <img src="lillesandogbirkenes.jpg" alt="" />
        <div>
        <button onClick={clearChat} className='clear-btn'>Ny Samtale</button> 
        <button onClick={sendToAdminPage} className='clear-btn'>Admin</button>
        </div>
      </div>
      <div className='line'></div>
      <div className='form-group files'>
        <label htmlFor="file" className="custom-file-upload">
          <i className=""></i>Last opp oppdatert dokument
        </label>
        <div className='file-input'>
        <input id="file" type="file" />
        </div>
      </div>
       <ChatBox chatLog={chatLog} isLoading={isLoading} handleSubmit={handleSubmit} input={input} setInput={setInput} />
       </div>
   );
 }

 export default Chat;
