import './App.css';
import './normal.css';
import { useState }  from 'react';

function App() {

  const [input, setInput] = useState('');
  const [chatLog, setChatLog] = useState([{
    user: 'gpt',
    message: 'How can I help you?'
  }]);


  async function handleSubmit(e) {
    e.preventDefault();
    setInput('');
    setChatLog([...chatLog, { use: "me", message: '${input}' }]);
  }

  return (
    <div className="App">
      <aside className='sidemenu'>
        <div className='sidemenu-btn'>
          <span>+</span>
          Ny Samtale
        </div>
      </aside>
      <section className='chatbox'>
        <div className='chat-log'>
          {chatLog.map((message, index) => (
            <ChatMessage message={message} />
          ))}


          <div className='chat-message chat-gpt'>
              <div className='chat-message-center'>
                <div className='avatar chat-gpt'></div>
                <div className='message'>OpenAI is a research organization based in San Francisco, California, that focuses on creating and promoting friendly AI in a way that benefits humanity. OpenAI's goal is to develop and promote AI in a responsible and safe way, with a focus on research in machine learning and deep learning algorithms, and building practical AI applications. OpenAI was founded in 2015 by Elon Musk, Sam Altman, Greg Brockman, Ilya Sutskever, Wojciech Zaremba, and John Schulman.</div>
            </div>
          </div>
        </div>
          <div className='chat-input-holder'>
            <form onSubmit={handleSubmit}>
                <input className='chat-input-textarea' value={input} onChange={() => setInput(e.target.vlaue)}></input>
            </form>

          </div>
      </section>
      </div>
  );
}

const ChatMessage = ({ message }) => {
  return (
    <div className={'chat-message ${message.user == "gpt" && "chatgpt"}'}>
    <div className='chat-message-center'>
      <div className={'avatar ${message.user == "gpt" && "chatgpt"}'}></div>
      <div className='message'>{message.message}</div>
  </div>
</div>

  );
}

export default App;
