 import '../App.css';
 import '../normal.css';
 import { useState }  from 'react';
 import SideMenu from '../components/SideMenu';
 import ChatBox from '../components/ChatBox';


 function Chat() {

   const [input, setInput] = useState('');
   const [chatLog, setChatLog] = useState([]);
   const [isLoading, setIsLoading] = useState(false);

 function clearChat() {
   setChatLog([]);
 }

 async function handleSubmit(e) {
     e.preventDefault();
     let chatLogNew = [...chatLog, { user: "me", message: `${input}`}];
     setInput('');
     setChatLog(chatLogNew);
     setIsLoading(true);

     const messages = chatLogNew.map((message) => message.message);  

     if (file === null) {
     const response = await fetch('http://localhost:3080/chat', {
       method: 'POST',
       body: messages
       });
       const data = await response.json();
       setChatLog([...chatLogNew, { user: 'gpt', message: data.message }]);
       setIsLoading(false);
   } else {
       const response = await fetch('http://localhost:3080/file', {
       method: 'POST',
       body: formData
       });
       const data = await response.json();
       setChatLog([...chatLogNew, { user: 'gpt', message: data.message }]);
       setIsLoading(false);
   }
 }

   return (
    
     <div className="App">
       <ChatBox chatLog={chatLog} isLoading={isLoading} handleSubmit={handleSubmit} input={input} setInput={setInput} />
       </div>
   );
 }

 export default Chat;
